// Robust JSON extractor for Gemini outputs (handles ```json ... ```, prose, etc.)
function extractJson(raw) {
  if (raw == null) return null;
  let s = String(raw).trim();

  // 1) Strip code fences like ```json ... ``` or ``` ... ```
  const fenceMatch = s.match(/```(?:json)?\s*([\s\S]*?)\s*```/i);
  if (fenceMatch) s = fenceMatch[1].trim();

  // 2) Remove zero-width and non-printing chars that sometimes sneak in
  s = s.replace(/[\u200B-\u200D\uFEFF]/g, "");

  // 3) If there's still extra text, grab from first '{' to last '}'
  const first = s.indexOf("{");
  const last = s.lastIndexOf("}");
  if (first !== -1 && last !== -1 && last > first) {
    s = s.slice(first, last + 1);
  }

  // 4) Parse
  try {
    return JSON.parse(s);
  } catch (e) {
    return null;
  }
}

const results = [];
for (const item of $input.all()) {
  const RAW = item.json.output ?? item.json.text ?? item.json;
  const parsed = extractJson(RAW);

  if (!parsed) {
    results.push({
      json: {
        success: false,
        items: [],
        log: "Parser error: invalid JSON from model.",
        raw: RAW,
      }
    });
    continue;
  }

  const success = !!parsed.success;
  const delimiter = parsed.delimiter || "|||~D~|||"; // your shortened delimiter
  const importList = typeof parsed.import_list === "string" ? parsed.import_list : "";
  const items = success
    ? importList.split(delimiter).map(s => s.trim()).filter(Boolean)
    : [];

  results.push({
    json: {
      success,
      items,
      log: success ? undefined : (parsed.log || "Nessun elemento valido."),
      raw: parsed,
    }
  });
}

return results;

const base = $("Prepare import batch (bulk)").first().json; // user_id, items_norm, norm_map, now
// console.log(base)

// Build Set of existing normalized values from Find results
const existing = new Set($input.all().map(i => String(i.json.value || '').trim()));
// console.log(existing)

const docs = [];
for (const n of base.items_norm) {
  if (existing.has(n)) continue; // skip duplicates

  docs.push({
    json: {
      user_id: base.user_id,
      value: n,                 // original text
      created_at: base.now,
      last_sent_at: null,
      last_interaction_at: null,
      reps: 0,
      lapses: 0,
      success_streak: 0,
      ewma_grade: 0.0,
      last_grade: 0,
      pending_message_id: null,
      status: "active"
    }
  });
}

return docs; // returns [] if nothing new

const userId = $('If<10 char').first().json.message.from.id; // adjust if needed
const now = new Date().toISOString();
const arr = $input.first().json.items

function norm(s) {
  return String(s)
    .normalize('NFKC')
    .toLowerCase()
    .replace(/\s+/g, ' ')
    .trim();
}

const norm_map = {};
for (const v of arr) {
  const n = norm(v);
  if (!n) continue;
  if (!(n in norm_map)) norm_map[n] = String(v).trim();
}

return [{
  json: {
    user_id: String(userId),
    items_norm: Object.keys(norm_map),
    norm_map,
    now
  }
}];

