// Loop over input items and add a new field called 'myNewField' to the JSON of each one
const items_list = $input.all().map(i => i.json.value);

let final_list;

if (items_list.length <= 30) {
  final_list = items_list;
} else {
  // Shuffle & keep 30
  function shuffle(array) {
    for (let i = array.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [array[i], array[j]] = [array[j], array[i]];
    }
    return array;
  }
  final_list = shuffle([...items_list]).slice(0, 30);
}

return [
  {
    json: {
      word_list: final_list
    }
  }
];

// Get the raw LLM output (adjust if your field is different)
function markdownToHtml(text) {
  // replace **bold** with <b>bold</b>
  return text.replace(/\*\*(.+?)\*\*/g, '<b>$1</b>');
}

const raw = ($input.first().json.output ?? '').trim();

if (!raw) {
  return [{ json: { error: 'Empty LLM output' } }];
}

// Split on "MSG<number>:" headers
const parts = raw.split(/(?=MSG\d+:)/g);

const items = [];

for (const part of parts) {
  const match = part.match(/^MSG(\d+):\s*([\s\S]*)$/);
  if (match) {
    const index = Number(match[1]);
    const text = match[2].trim();
    if (text) {
      items.push({
        msg_index: index,
        text
      });
    }
  }
}

// Sort just in case and return as multiple items
items.sort((a, b) => a.msg_index - b.msg_index);

return items.map(({ msg_index, text }) => ({
  json: { msg_index, text: markdownToHtml(text) }
}));
